{{- with $config.query_url }}
  {{- $searchUrl = printf "%s%s" $.Site.BaseURL . }}
{{- else }}
  {{- $searchUrl = printf "%s%s" $.Site.BaseURL "search/?q={searchTerms}" }}
  {{- partial "func/debug"
    (dict "message" (printf "opensearch: using default site search URL (see %s)" $config.hookLink) "context" . "severity" "info" "level" 8)
  -}}
{{- end }}
{{- $searchForm := "" }}
{{- with $config.search_form }}
  {{- $searchForm = printf "%s%s" $.Site.BaseURL . }}
{{- else }}
  {{- $searchForm = printf "%s%s" $.Site.BaseURL "search/" }}
  {{- partial "func/debug"
    (dict "message" (printf "opensearch: using default site search form URL (see %s)" $config.hookLink) "context" . "severity" "info" "level" 8)
  -}}
{{- end }}
{{- $image := "" }}
