OpenSearchDescription:
{{- $config := site.Params.dnb.opensearch }}
{{- $autoupdate := $config.enable_autoupdate | default true }}
{{- $title := $config.title | default (printf "%s" site.Title) }}
{{- if gt (len $title) 16 }}
  {{- partial "func/debug"
    (dict
      "message" (printf "opensearch: parameter %s must be 1024 or less characters (see %s)" $config.hookLink)
      "context" .
      "severity" "debug"
    )
  -}}
{{- end }}
{{- $description := $config.description | default (printf "%s %s" "Search on" site.Title) }}
{{- if gt (len $description) 1024 }}
  {{- partial "func/debug"
    (dict
      "message" (printf "opensearch: parameter %s must be 1024 or less characters (see %s)" $config.hookLink)
      "context" .
      "severity" "debug"
    )
  -}}
{{- end }}
{{- $searchUrl := "" }}
{{- with $config.query_url }}
  {{- $searchUrl = printf "%s%s" site.BaseURL . }}
{{- else }}
  {{- $searchUrl = printf "%s%s" site.BaseURL "search/?q={searchTerms}" }}
  {{- partial "func/debug"
    (dict
      "message" (printf "opensearch: using default site search URL (see %s)" $config.hookLink)
      "context" .
      "severity" "debug"
    )
  -}}
{{- end }}
{{- $searchForm := "" }}
{{- with $config.search_form }}
  {{- $searchForm = printf "%s%s" site.BaseURL . }}
{{- else }}
  {{- $searchForm = printf "%s%s" site.BaseURL "search/" }}
  {{- partial "func/debug"
    (dict
      "message" (printf "opensearch: using default site search form URL (see %s)" $config.hookLink)
      "context" .
      "severity" "debug"
    )
  -}}
{{- end }}
{{- $image := "" }}
{{- with $config.image }}
  {{- $image = printf "%s%s" site.BaseURL . }}
{{- else }}
  {{- $image = printf "%s%s" site.BaseURL "favicon.png" }}
  {{- partial "func/debug"
    (dict
      "message" (printf "opensearch: using default site search icon (see %s)" $config.hookLink)
      "context" .
      "severity" "debug"
    )
  -}}
{{- end -}}
{{- $title -}}
{{- $description -}}

{{- $image -}}
{{- $searchForm -}}
